import React, { Component } from "react";
import "./App.css";
import Person from "./Person/Person";

class App extends Component {
  state = {
    //this is an array of person
    person: [
      { name: "braj", age: "28" },
      { name: "kishore", age: "25" },
      { name: "test", age: "15" }
    ],
    otherState: "some other value",
    showPersons: false
  };

  switchNameHandler = newName => {
    console.log("was clicked ");
    // react will not recognise this change and will not pick up
    //this.state.person[0].name = "braj kisore";

    // updating dom , this setstate available only in class component
    this.setState({
      person: [
        { name: newName, age: "28" },
        { name: "kishore", age: "25" },
        { name: "dubey", age: "15" }
      ]
    });
  };

  nameChangeHandler = event => {
    this.setState({
      person: [
        { name: "bk", age: "28" },
        { name: event.target.value, age: "25" },
        { name: "dubey", age: "15" }
      ]
    });
  };

  deletePersonHandler = personIndex => {
    const persons = this.state.persons;
    persons.splice(personIndex, 1); // for removing element from array
    this.setState({ persons: persons });
  };

  togglePersonHandler = () => {
    console.log("toggle handling");
    const doesShow = this.state.showPersons;
    this.setState({ showPersons: !doesShow });
  };
  render() {
    const style = {
      backgroundColor: "white",
      font: "inherit",
      border: "1px solid blue",
      padding: "8px",
      cursor: "pointer"
    };

    let persons = null;
    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.person.map(person, index => {
            return (
              <Person
                click={this.deletePersonHandler()}
                name={person.name}
                age={person.age}
              />
            );
          })}
        </div>
      );
    } else {
    }
    return (
      <div className="App">
        <h1> hello react</h1>
        <p>this is really working !</p>
        <button style={style} onClick={this.togglePersonHandler}>
          {/* this is comment
          onClick={this.switchNameHandler.bind(this, "brajkishore")
          or can use - 
            () => this.switchNameHandler("BrajKishore!")
            in above syntax implicitly added return statement after "=>"
            now we can add paramter "()" it will execute in onclick only 

            recommended :- bind syntax
          */}{" "}
          Toggle person{" "}
        </button>
        {persons}
      </div>
    );
  }
}

export default App;

function App1() {
  return (
    <div className="App">
      <h1> hello react</h1>
    </div>
  );
}
